import { useState } from "react";
import "@fontsource/inria-sans";
import { useNavigate } from "react-router-dom";
import "../styles.css";

export default function LoginFuncionario() {
  const [rut, setRut] = useState("");
  const [clave, setClave] = useState("");
  const [mensaje, setMensaje] = useState("");
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch("http://127.0.0.1:5000/api/login", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ rut, clave }),
      });

      if (!response.ok) {
        setMensaje("Contrase침a o RUT incorrecto");
        return;
      }

      setMensaje("Datos enviados correctamente!");
      navigate("/dashboard-funcionario");
    } catch (error) {
      setMensaje("Ocurri칩 un error al enviar los datos");
    }
  };

  return (
    <div className="vista2-container">
      <div className="banner">
        <div className="logo" style={{ backgroundImage: "url('/image.png')" }}></div>
        <div className="red-eleam"></div>
      </div>

      <div className="bienvenida">Te damos la bienvenida!</div>

      <div className="login-box">
        <div className="login-titulo">Ingresa Portal ELEAM</div>
        <form onSubmit={handleSubmit}>
          <div style={{ position: "relative" }}>
            <input
              type={mostrarClave ? "text" : "password"}
              placeholder="Ingresa tu Clave"
              value={clave}
              onChange={(e) => setClave(e.target.value)}
              style={{ paddingRight: "40px" }}
            />
            <button
              type="button"
              onClick={() => setMostrarClave((v) => !v)}
              style={{
                position: "absolute",
                right: 0,
                top: 0,
                height: "100%",
                background: "none",
                border: "none",
                cursor: "pointer"
              }}
              tabIndex={-1}
            >
              {mostrarClave ? "Ocultar" : "Ver"}
            </button>
          </div>
          <button type="submit" className="btn-ingresar">Ingresar</button>
        </form>
        {mensaje && <p className="mensaje">{mensaje}</p>}
      </div>
    </div>
  );
}
