import React from "react";
import "../styles.css";
import "@fontsource/inria-sans";

export default function LoginFuncionario() {
  const [rut, setRut] = useState("");
  const [clave, setClave] = useState("");
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch("http://127.0.0.1:5000/api/funcionarios", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ rut, clave }),
      });

      if (!response.ok) {
        console.error("Error en la respuesta del servidor");
        return;
      }
    } catch (error) {
      console.error("Error enviando datos:", error);
    }
  };

  return (
    <div className="vista2-container">
      {/* Banner */}
      <div className="banner">
        <div
          className="logo"
          style={{ backgroundImage: "url('/image.png')" }}
        ></div>
        <div className="red-eleam">Red ELEAM</div>
      </div>

      {/* Bienvenida */}
      <div className="bienvenida">Te damos la bienvenida!</div>

      {/* Login Box */}
      <div className="login-box">
        <div className="login-titulo">Ingresa Portal ELEAM</div>
        <input type="text" placeholder="Ingresa tu RUN" />
        <input type="password" placeholder="Ingresa tu Clave" />
        <button className="btn-ingresar">Ingresar</button>
      </div>
    </div>
  );
}
