import { useState, useEffect } from "react";
import FormularioFuncionario from "./FormularioFuncionario";

export default function DashboardFuncionario({ usuario, setUsuario }) {
  const [funcionarios, setFuncionarios] = useState([]);
  const [editing, setEditing] = useState(null);

  const fetchFuncionarios = async () => {
    const res = await fetch("http://localhost:5000/api/funcionarios", {
      credentials: "include"
    });
    const data = await res.json();
    setFuncionarios(data);
  };

  useEffect(() => {
    fetchFuncionarios();
  }, []);

  const handleLogout = async () => {
    await fetch("http://localhost:5000/api/logout", { method: "POST", credentials: "include" });
    setUsuario(null);
  };

  const handleEliminar = async (rut) => {
    await fetch(`http://localhost:5000/api/funcionarios/${rut}`, { method: "DELETE", credentials: "include" });
    fetchFuncionarios();
  };

  return (
    <div>
      <h2>Bienvenido, {usuario}</h2>
      <button onClick={handleLogout}>Cerrar Sesión</button>
      <h3>Lista de Funcionarios</h3>
      <button onClick={() => setEditing({})}>Añadir Nuevo</button>

      {editing && <FormularioFuncionario funcionario={editing} setEditing={setEditing} refresh={fetchFuncionarios} />}

      <table border="1">
        <thead>
          <tr>
            <th>RUT</th>
            <th>Nombres</th>
            <th>Apellidos</th>
            <th>Cargo</th>
            <th>Turno</th>
            <th>Asistencia</th>
            <th>Acciones</th>
          </tr>
        </thead>
        <tbody>
          {funcionarios.map(f => (
            <tr key={f.rut}>
              <td>{f.rut}</td>
              <td>{f.nombres}</td>
              <td>{f.apellidos}</td>
              <td>{f.cargo}</td>
              <td>{f.turno}</td>
              <td>{f.asistencia}</td>
              <td>
                <button onClick={() => setEditing(f)}>Editar</button>
                <button onClick={() => handleEliminar(f.rut)}>Eliminar</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
