import { useState } from "react";
import "@fontsource/inria-sans";
import { useNavigate } from "react-router-dom";
import "../LoginFuncionario.css";
export default function LoginFuncionario() {
  const [rut, setRut] = useState("");
  const [clave, setClave] = useState("");
  const [mensaje, setMensaje] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch("http://127.0.0.1:5000/api/funcionarios", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ rut, clave }),
      });

      if (!response.ok) {
        console.error("Error en la respuesta del servidor");
        setMensaje("Error al enviar los datos");
        return;
      }

      setMensaje("Datos enviados correctamente!");
      // No se redirige, se queda en la misma p치gina
    } catch (error) {
      console.error("Error enviando datos:", error);
      setMensaje("Ocurri칩 un error al enviar los datos");
    }
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Ingrese RUN del funcionario"
          value={rut}
          onChange={(e) => setRut(e.target.value)}
          required
        />
        <input
          type="password"
          placeholder="Ingrese su clave"
          value={clave}
          onChange={(e) => setClave(e.target.value)}
          required
        />
        <button type="submit">Ingresar</button>
      </form>
      {mensaje && <p>{mensaje}</p>}
    </div>
  );
}
