import { useState, useEffect } from "react";
import { useLocation } from "react-router-dom";
import "./Dashboard.css";

export default function Buscar() {
  const [residente, setResidente] = useState({ rut: "", nombre: "Pendiente de BD" });
  const location = useLocation();

  // Obtener el rut desde la query string
  useEffect(() => {
    const params = new URLSearchParams(location.search);
    const rutQuery = params.get("rut");
    if (rutQuery) setResidente(prev => ({ ...prev, rut: rutQuery }));
  }, [location]);

   return (
    <div className="dashboard-container">
      {/* Banner */}
      <header className="banner">
        <img src="/image.png" alt="Logo ELEAM" className="logo" />
        <h1 className="red-eleam">Red ELEAM</h1>
        <h2 className="portal-title">Portal de Medicamentos ELEAM</h2>
      </header>

      {/* Datos residente */}
      <div className="datos-residente">
        <div className="foto-residente"></div>
        <div className="info-residente">
          <p><b>Nombre residente:</b> {residente.nombre}</p>
          <p><b>RUN:</b> {residente.rut}</p>
          <p><b>Próximo control:</b> ..................</p>
          <p><b>Médico tratante:</b> ..................</p>
        </div>
      </div>

      {/* Botones de acción */}
      <div className="botones-accion">
        <button className="boton">📄 Ver Ficha Clínica</button>
        <button className="boton">ℹ️ Información Control Anterior</button>
        <button className="boton">💊 Información Medicamentos</button>
        <button className="boton">🚪 Salir</button>
      </div>

      {/* Diagnóstico */}
      <div className="diagnostico">
        <h3>Diagnóstico Residente</h3>
        <div className="diagnostico-detalle">
          <div>
            <p><b>Diagnóstico:</b> .......................................</p>
            <p><b>Médico que indica:</b> ......................</p>
          </div>
          <div>
            <p><b>Nombre Medicamento (Compuesto):</b> ....</p>
            <p><b>Dosis:</b> ..... cada ......</p>
            <p><b>Caso SOS:</b> .......................</p>
          </div>
        </div>
      </div>
    </div>
  );
}
