import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import "../HistorialClinico.css";

export default function HistorialClinico() {
  const { rut } = useParams();
  const [ficha, setFicha] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  useEffect(() => {
    const cargarHistorial = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/api/historial-clinico/${rut}`);
        setFicha(response.data);
        setError("");
      } catch (err) {
        console.error("Error al cargar historial:", err);
        setError("Error al cargar la ficha clínica");
      } finally {
        setLoading(false);
      }
    };

    cargarHistorial();
  }, [rut]);

  if (loading) return <p>Cargando historial clínico...</p>;
  if (error) return <p>{error}</p>;
  if (!ficha) return <p>No se encontró información para el RUT indicado.</p>;

  const signos = ficha.signos_vitales || {};

  return (
    <div className="historial-container">
      <h2>Ficha Clínica del Residente</h2>

      <div className="info-general">
        <p><strong>RUT:</strong> {ficha.rut}</p>
        <p><strong>Nombre:</strong> {ficha.nombre}</p>
        <p><strong>Médico tratante:</strong> {ficha.medico_tratante}</p>
        <p><strong>Diagnóstico:</strong> {ficha.diagnostico}</p>
        <p><strong>Próximo control:</strong> {ficha.proximo_control}</p>
      </div>

      <h3>Signos Vitales</h3>
      {Object.keys(signos).length > 0 ? (
        <table className="tabla-signos">
          <thead>
            <tr>
              <th>Fecha</th>
              <th>Hora</th>
              <th>Presión Sistólica</th>
              <th>Presión Diastólica</th>
            </tr>
          </thead>
          <tbody>
            <tr>
              <td>{signos.fecha}</td>
              <td>{signos.hora}</td>
              <td>{signos.presionSistolica}</td>
              <td>{signos.presionDiastolica}</td>
            </tr>
          </tbody>
        </table>
      ) : (
        <p>No se han registrado signos vitales para este residente.</p>
      )}
    </div>
  );
}
