// Dashboard.jsx
import { useState, useEffect } from "react";
import { useLocation } from "react-router-dom";
import "./Dashboard.css"; // tu CSS completo

export default function Dashboard() {
  const location = useLocation();
  const [residente, setResidente] = useState({
    rut: "",
    nombre: "Pendiente",
    proximoControl: "dd/mm/aaaa",
    medico: "Médico tratante",
    diagnostico: "Diagnóstico",
    medicamento: "Nombre Medicamento",
    dosis: "Dosis",
    casoSOS: "Caso SOS"
  });

  useEffect(() => {
    const params = new URLSearchParams(location.search);
    const rutQuery = params.get("rut");
    if (rutQuery) setResidente(prev => ({ ...prev, rut: rutQuery }));
  }, [location]);

  return (
    <div className="vista2">
      {/* Banner */}
      <div className="banner">
        <div className="logo"></div>
        <div className="red-eleam">Red ELEAM</div>
        <div className="portal-titulo">Portal de Medicamentos ELEAM</div>
        <div className="bell-icon"></div>
      </div>

      {/* Datos funcionario */}
      <div className="datos-funcionario">
        <div className="image"></div>
        <div className="residente-info">
          <p>Nombre residente: {residente.nombre}</p>
          <p>RUN: {residente.rut}</p>
          <p>Médico tratante: {residente.medico}</p>
          <p>Próximo control: {residente.proximoControl}</p>
        </div>
      </div>

      {/* Botones */}
      <div className="botones">
        <button className="boton1">Ver Ficha Clínica</button>
        <button className="boton2">Información Control Anterior</button>
        <button className="boton3">Información Medicamentos</button>
        <button className="boton4">Salir</button>
      </div>

      {/* Formulario / Diagnóstico */}
      <div className="formulario">
        <div className="diagnostico">
          <p>Diagnóstico: {residente.diagnostico}</p>
          <p>Médico que indica: {residente.medico}</p>
        </div>
        <div className="medicamento">
          <p>Nombre Medicamento (Compuesto): {residente.medicamento}</p>
          <p>Dosis: {residente.dosis}</p>
          <p>Caso SOS: {residente.casoSOS}</p>
        </div>
      </div>

      {/* Título formulario */}
      <div className="diagnostico-residente">Diagnóstico Residente</div>
    </div>
  );
}
