import { useState, useEffect } from "react";
import FormularioFuncionario from "./FormularioFuncionario";
import "../DashboardFuncionario.css"; // CSS independiente

export default function DashboardFuncionario({ usuario, setUsuario }) {
  const [funcionarios, setFuncionarios] = useState([]);
  const [editing, setEditing] = useState(null);

  const fetchFuncionarios = async () => {
    try {
      const res = await fetch("http://localhost:5000/api/funcionarios");
      const data = await res.json();
      setFuncionarios(data);
    } catch (error) {
      console.error("Error al obtener funcionarios:", error);
    }
  };

  useEffect(() => {
    fetchFuncionarios();
  }, []);

  const handleEliminar = async (rut) => {
    await fetch(`http://localhost:5000/api/funcionarios/${rut}`, { method: "DELETE" });
    fetchFuncionarios();
  };

  return (
    <div className="dashboard-funcionario">
      <h2>Bienvenido {usuario}</h2>
      <button onClick={() => setEditing({})}>AÃ±adir Nuevo</button>

      {editing && <FormularioFuncionario funcionario={editing} setEditing={setEditing} refresh={fetchFuncionarios} />}

      <table border="1">
        <thead>
          <tr>
            <th>RUT</th>
            <th>Nombres</th>
            <th>Apellidos</th>
            <th>Cargo</th>
            <th>Turno</th>
            <th>Asistencia</th>
            <th>Acciones</th>
          </tr>
        </thead>
        <tbody>
          {funcionarios.map(f => (
            <tr key={f.rut}>
              <td>{f.rut}</td>
              <td>{f.nombres}</td>
              <td>{f.apellidos}</td>
              <td>{f.cargo}</td>
              <td>{f.turno}</td>
              <td>{f.asistencia}</td>
              <td>
                <button onClick={() => setEditing(f)}>Editar</button>
                <button onClick={() => handleEliminar(f.rut)}>Eliminar</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
