import { useState } from "react";
import "@fontsource/inria-sans";
import "../FormularioFuncionario.css";

export default function FormularioFuncionario({ funcionario, setEditing, refresh }) {
  const [rut, setRut] = useState(funcionario.rut || "");
  const [nombres, setNombres] = useState(funcionario.nombres || "");
  const [apellidos, setApellidos] = useState(funcionario.apellidos || "");
  const [cargo, setCargo] = useState(funcionario.cargo || "");
  const [turno, setTurno] = useState(funcionario.turno || ""); // fecha de ingreso
  const [asistencia, setAsistencia] = useState(
    typeof funcionario.asistencia === "boolean" ? funcionario.asistencia : false
  );

  const handleSubmit = async () => {
    const url = funcionario.rut
      ? `http://localhost:5000/api/funcionarios/${rut}`
      : "http://localhost:5000/api/funcionarios";

    const method = funcionario.rut ? "PUT" : "POST";

    const data = { rut, nombres, apellidos, cargo, turno, asistencia };

    try {
      const res = await fetch(url, {
        method,
        headers: { "Content-Type": "application/json" },
        credentials: "include",
        body: JSON.stringify(data),
      });

      if (!res.ok) throw new Error("Error al guardar funcionario");

      refresh();
      setEditing(null);
    } catch (err) {
      console.error(err);
      alert("No se pudo guardar el funcionario. Revisa la consola.");
    }
  };

  return (
    <div className="formulario-funcionario">
      <h3>{funcionario.rut ? "Editar Funcionario" : "Nuevo Funcionario"}</h3>
      <input
        placeholder="RUT"
        value={rut}
        onChange={e => setRut(e.target.value)}
        disabled={!!funcionario.rut}
      />
      <input
        placeholder="Nombres"
        value={nombres}
        onChange={e => setNombres(e.target.value)}
      />
      <input
        placeholder="Apellidos"
        value={apellidos}
        onChange={e => setApellidos(e.target.value)}
      />
      <input
        placeholder="Cargo"
        value={cargo}
        onChange={e => setCargo(e.target.value)}
      />
      <input
        type="date"
        value={turno}
        onChange={e => setTurno(e.target.value)}
      />
      <label>
        Asistencia:
        <input
          type="checkbox"
          checked={asistencia}
          onChange={e => setAsistencia(e.target.checked)}
        />
      </label>
      <button onClick={handleSubmit}>
        {funcionario.rut ? "Actualizar" : "Crear"}
      </button>
      <button onClick={() => setEditing(null)}>Cancelar</button>
    </div>
  );
}
