import { useState } from "react";

export default function LoginForm() {
  const [rut, setRut] = useState("");
  const [fecha, setFecha] = useState("");
  const navigate = useNavigate();
  
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch("http://localhost:5000/api/residentes", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ rut, fecha }),
      });
      if (response.ok){
        alert("Datos enviados correctamente")
        Navigate('/dashboard', { state: { usuario: { rut, fecha } } })
      } 
    } catch (error) {
      console.error("Error enviando datos:", error);
    }
  };

  return (
    <div className="login-page">
      <header className="banner">
        <div className="logo-container">
          <img src="/img/logo.png" alt="Logo ELEAM" className="logo" />
          <h1 className="logo-text">Red ELEAM</h1>
        </div>
      </header>

      <h2 className="bienvenida">Â¡Te damos la bienvenida!</h2>

      <div className="login-box">
        <h3 className="title">Portal de Medicamentos ELEAM</h3>

        <form onSubmit={handleSubmit}>
          <input
            type="text"
            placeholder="Ingrese RUN del residente"
            value={rut}
            onChange={(e) => setRut(e.target.value)}
            required
          />
          <input
            type="date"
            value={fecha}
            onChange={(e) => setFecha(e.target.value)}
            required
          />
          <button type="submit">Ingresar</button>
        </form>
      </div>
    </div>
  );
}
