import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import "../HistorialClinico.css";

export default function HistorialClinico() {
  const { rut } = useParams();
  const [signos, setSignos] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  useEffect(() => {
    const cargarHistorial = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/api/historial-clinico/${rut}`);
        setSignos(response.data);
        setError("");
      } catch (err) {
        setError("Error al cargar el historial clínico");
        console.error(err);
      } finally {
        setLoading(false);
      }
    };

    cargarHistorial();
  }, [rut]);

  if (loading) return <p>Cargando historial...</p>;
  if (error) return <p>{error}</p>;
  if (!signos || Object.keys(signos).length === 0)
    return <p>No hay signos vitales registrados para este residente.</p>;

  return (
    <div className="historial-container">
      <h2>Historial Clínico</h2>
      <table>
        <thead>
          <tr>
            <th>Fecha</th>
            <th>Hora</th>
            <th>Presión Sistólica</th>
            <th>Presión Diastólica</th>
          </tr>
        </thead>
        <tbody>
          <tr>
            <td>{signos.fecha}</td>
            <td>{signos.hora}</td>
            <td>{signos.presionSistolica}</td>
            <td>{signos.presionDiastolica}</td>
          </tr>
        </tbody>
      </table>
    </div>
  );
}
